CREATE OR REPLACE FUNCTION pls_obter_dt_ultimo_reajuste ( nr_seq_contrato_p bigint, ie_tipo_contrato_p text, ie_deflator_p text default 'N') RETURNS timestamp AS $body$
DECLARE


dt_retorno_w			timestamp;


BEGIN

if (ie_tipo_contrato_p = 'PF') then
	select	max(z.dt_reajuste)
	into STRICT	dt_retorno_w
	from	pls_reajuste		z,
		pls_reajuste_tabela	x
	where	x.nr_seq_reajuste	= z.nr_sequencia
	and	x.nr_seq_contrato	= nr_seq_contrato_p
	and	z.ie_status		= '2';
elsif (ie_tipo_contrato_p = 'PJ') then
	select	max(z.dt_reajuste)
	into STRICT	dt_retorno_w
	from	pls_reajuste	z
	where	z.nr_seq_contrato	= nr_seq_contrato_p
	and	z.ie_status		= '2'
	and	((ie_deflator_p = 'S') or (coalesce(z.nr_seq_lote_deflator::text, '') = '' and	coalesce(z.tx_deflator::text, '') = ''));
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
