CREATE OR REPLACE FUNCTION pls_obter_edicao_preco_proc ( cd_estabelecimento_p bigint, nr_seq_prestador_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_vigencia_p timestamp) RETURNS bigint AS $body$
DECLARE


cd_area_w		bigint	:= 0;
cd_especialidade_w	bigint	:= 0;
cd_grupo_w		bigint	:= 0;
cd_edicao_amb_w		bigint;
cd_edicao_amb_ww	bigint;

C01 CURSOR FOR
	SELECT	/* +CHOOSE */
		cd_edicao_amb
	from	pls_regra_preco_proc
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	((coalesce(nr_seq_prestador::text, '') = '') or (nr_seq_prestador = nr_seq_prestador_p))
	and	coalesce(cd_procedimento,cd_procedimento_p) 		= cd_procedimento_p
	and	coalesce(ie_origem_proced,ie_origem_proced_p)	= ie_origem_proced_p
	and	coalesce(cd_area_procedimento,cd_area_w)		= cd_area_w
	and	coalesce(cd_especialidade,cd_especialidade_w)	= cd_especialidade_w
	and	coalesce(cd_grupo_proc,cd_grupo_w)			= cd_grupo_w
	and	coalesce(ie_situacao,'A')	= 'A'
	and	dt_inicio_vigencia	=
		(SELECT	max(dt_inicio_vigencia)
		from	pls_regra_preco_proc	a
		where	a.cd_estabelecimento	= cd_estabelecimento_p
		and	((coalesce(a.nr_seq_prestador::text, '') = '') or (a.nr_seq_prestador = nr_seq_prestador_p))
		and	coalesce(cd_procedimento,cd_procedimento_p) 		= cd_procedimento_p
		and	coalesce(ie_origem_proced,ie_origem_proced_p)	= ie_origem_proced_p
		and	coalesce(cd_area_procedimento,cd_area_w)		= cd_area_w
		and	coalesce(cd_especialidade,cd_especialidade_w)	= cd_especialidade_w
		and	coalesce(cd_grupo_proc,cd_grupo_w)			= cd_grupo_w
		and 	coalesce(ie_situacao,'A')	= 'A'
		and	a.dt_inicio_vigencia 	<= dt_vigencia_p);


BEGIN
cd_edicao_amb_w	:= null;

begin /* Obter Estrutura do procedimento */
select	cd_grupo_proc,
	cd_especialidade,
	cd_area_procedimento
into STRICT	cd_grupo_w,
	cd_especialidade_w,
	cd_area_w
from	estrutura_procedimento_v
where	cd_procedimento 	= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;
exception
when others then
begin
cd_grupo_w		:= 0;
cd_especialidade_w	:= 0;
cd_area_w		:= 0;
end;
end;

open C01;
loop
fetch C01 into
	cd_edicao_amb_ww;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (cd_edicao_amb_ww IS NOT NULL AND cd_edicao_amb_ww::text <> '') then
		cd_edicao_amb_w	:= cd_edicao_amb_ww;
	end if;
	end;
end loop;
close C01;
return	cd_edicao_amb_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
