SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_elegibilidade_benef ( nr_seq_elegibilidade_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(10)	:= 'true';
cd_usuario_plano_w		varchar(30);
qt_benef_elegibilidade_w	bigint;
nm_beneficiario_w		varchar(255);


BEGIN

select	cd_usuario_plano,
	upper(nm_beneficiario)
into STRICT	cd_usuario_plano_w,
	nm_beneficiario_w
from	pls_elegibilidade
where	nr_sequencia	= nr_seq_elegibilidade_p;

select	count(*)
into STRICT	qt_benef_elegibilidade_w
from	pls_segurado a,
	pls_segurado_carteira b,
	pessoa_fisica c
where	a.nr_sequencia		= b.nr_seq_segurado
and	a.cd_pessoa_fisica	= c.cd_pessoa_fisica
and	b.cd_usuario_plano	= cd_usuario_plano_w
and	upper(c.nm_pessoa_fisica)	= nm_beneficiario_w;

if (qt_benef_elegibilidade_w = 0) then
	ds_retorno_w	:= 'false';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
