SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_email_cooperativa ( cd_cooperativa_p text) RETURNS varchar AS $body$
DECLARE


ds_email_w		varchar(255);
cd_cgc_w		varchar(20);


BEGIN

begin
select	max(cd_cgc)
into STRICT	cd_cgc_w
from	pls_congenere
where	cd_cooperativa	= cd_cooperativa_p;
exception
when others then
	cd_cgc_w	:= '';
end;

if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	select	max(ds_email)
	into STRICT	ds_email_w
	from	pessoa_juridica
	where	cd_cgc	= cd_cgc_w;
end if;

return	ds_email_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_email_cooperativa ( cd_cooperativa_p text) FROM PUBLIC;

