SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_email_pagador (cd_cgc_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Obter email do pagador, é utilizado na função OPS - AME
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[x]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_email_retorno_w	pls_contrato_pagador.ds_email%type	:= null;
nr_seq_w		pls_contrato_pagador.nr_sequencia%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_w
from	pls_contrato_pagador
where	cd_cgc = cd_cgc_p
and	(ds_email IS NOT NULL AND ds_email::text <> '');

if (nr_seq_w IS NOT NULL AND nr_seq_w::text <> '') then
	select 	ds_email
	into STRICT	ds_email_retorno_w
	from	pls_contrato_pagador
	where	nr_sequencia = nr_seq_w;
end if;

return	ds_email_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_email_pagador (cd_cgc_p bigint) FROM PUBLIC;

