CREATE OR REPLACE FUNCTION pls_obter_email_pessoa ( cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_email_w	varchar(2000);

BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		ds_email_w := substr(obter_mail_pf_concat(cd_pessoa_fisica_p),1,2000);
	else
		begin
		select	substr(coalesce(b.ds_email,a.ds_email),1,2000)
		into STRICT	ds_email_w
		FROM pessoa_juridica a
LEFT OUTER JOIN pessoa_juridica_estab b ON (a.cd_cgc = b.cd_cgc)
WHERE coalesce(b.cd_estabelecimento, cd_estabelecimento_p) = cd_estabelecimento_p and a.cd_cgc = cd_cgc_p;
		exception
		when others then
			ds_email_w	:= '';
		end;
	end if;
end if;

return ds_email_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
