SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_email_vendedor ( nr_seq_vendedor_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_email_w		varchar(4000);
cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(14);


BEGIN

select	cd_pessoa_fisica,
	cd_cgc
into STRICT	cd_pessoa_fisica_w,
	cd_cgc_w
from	pls_vendedor
where	nr_sequencia	= nr_seq_vendedor_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	select	max(ds_email)
	into STRICT	ds_email_w
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w
	and	ie_tipo_complemento	= 1;
elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	select	max(ds_email)
	into STRICT	ds_email_w
	from	pessoa_juridica_estab
	where	cd_cgc	= cd_cgc_w;
end if;

ds_retorno_w	:= substr(ds_email_w,1,255);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_email_vendedor ( nr_seq_vendedor_p bigint) FROM PUBLIC;

