CREATE OR REPLACE FUNCTION pls_obter_empresa_carteira ( cd_operadora_empresa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
C - CNPJ
N - Nome
*/
ds_retorno_w	varchar(250);
cd_cgc_w	varchar(14);


BEGIN


select	max(cd_cgc)
into STRICT	cd_cgc_w
from	pessoa_juridica
where	cd_operadora_empresa	= cd_operadora_empresa_p;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= cd_cgc_w;
elsif (ie_opcao_p = 'N') then
	select	max(ds_razao_social)
	into STRICT	ds_retorno_w
	from	pessoa_juridica
	where	cd_cgc	= cd_cgc_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
