CREATE OR REPLACE FUNCTION pls_obter_empresa_pagador ( nr_seq_titulo_cobr_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* ie_opcao_p
	'E'  = Empresa
	'M' = Matr√≠cula
*/
cd_matricula_w		varchar(20);
ds_empresa_w		varchar(255);
ds_retorno_w		varchar(255);


BEGIN

select	substr(obter_razao_social(max(c.cd_cgc)),1,255),
	max(b.cd_matricula)
into STRICT	ds_empresa_w,
	cd_matricula_w
from	pls_mensalidade		a,
	pls_contrato_pagador_fin b,
	pls_desc_empresa	c,
	cobranca_escritural	d,
	titulo_receber_cobr	e
where	a.nr_seq_pagador	= b.nr_seq_pagador
and	b.nr_seq_empresa	= c.nr_sequencia
and	d.dt_remessa_retorno between coalesce(b.dt_inicio_vigencia, d.dt_remessa_retorno) and coalesce(b.dt_fim_vigencia, d.dt_remessa_retorno)
and	a.nr_sequencia		= e.nr_seq_mensalidade
and	d.nr_sequencia		= e.nr_seq_cobranca
and	e.nr_sequencia		= nr_seq_titulo_cobr_p;

if (ie_opcao_p = 'E') then
	ds_retorno_w	:= ds_empresa_w;
elsif (ie_opcao_p = 'M') then
	ds_retorno_w	:= cd_matricula_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
