SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_endereco_segurado ( nr_seq_segurado_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(4000);
cd_pessoa_fisica_w	varchar(10);
sg_estado_w		compl_pessoa_fisica.sg_estado%type;
ds_endereco_w		varchar(100);
ds_bairro_w		varchar(100);
nr_endereco_w		bigint;
ds_municipio_w		varchar(100);

/*E = ESTADO
    R = RUA
    B = BAIRRO
    M = MUNICIPIO
    N = NUMERO*/
BEGIN
select	cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from	pls_segurado
where	nr_sequencia = nr_seq_segurado_p;

select	coalesce(sg_estado,'X')
into STRICT	sg_estado_w
from	compl_pessoa_fisica
where	cd_pessoa_fisica 	= cd_pessoa_fisica_w
and	ie_tipo_complemento	= 1;

select	substr(ds_bairro,1,100)
into STRICT	ds_bairro_w
from	compl_pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_w
and	ie_tipo_complemento	= 1;

select	substr(ds_endereco,1,100)
into STRICT	ds_endereco_w
from	compl_pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_w
and	ie_tipo_complemento	= 1;

select	substr(nr_endereco,1,10)
into STRICT	nr_endereco_w
from	compl_pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_w
and	ie_tipo_complemento	= 1;

select	substr(ds_municipio,1,100)
into STRICT	ds_municipio_w
from	compl_pessoa_fisica
where	cd_pessoa_fisica = cd_pessoa_fisica_w
and	ie_tipo_complemento	= 1;

if (ie_opcao_p 	= 'E')	then
	ds_retorno_w	:= sg_estado_w;
elsif (ie_opcao_p	= 'R')	then
	ds_retorno_w	:= ds_endereco_w;
elsif (ie_opcao_p	= 'B')	then
	ds_retorno_w	:= ds_bairro_w;
elsif (ie_opcao_p	= 'M')	then
	ds_retorno_w	:= ds_municipio_w;
elsif (ie_opcao_p	= 'N')	then
	ds_retorno_w	:= nr_endereco_w;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_endereco_segurado ( nr_seq_segurado_p bigint, ie_opcao_p text) FROM PUBLIC;

