CREATE OR REPLACE FUNCTION pls_obter_end_compl_pj ( cd_cgc_p text, nr_seq_compl_pj bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);			
ds_endereco_w		varchar(40);	
nr_endereco_w		varchar(20);
ds_complemento_w	varchar(40);
ds_bairro_w		varchar(40);
sg_estado_w		pessoa_juridica_compl.sg_estado%type;
ds_municipio_w		varchar(40);			
			 

BEGIN 
select substr(ds_endereco,1,38), 
	CASE WHEN coalesce(nr_endereco::text, '') = '' THEN  null  ELSE ', '||substr(nr_endereco,1,18) END , 
	CASE WHEN coalesce(ds_complemento::text, '') = '' THEN  null  ELSE ', '||substr(ds_complemento,1,38) END , 
	CASE WHEN coalesce(ds_bairro::text, '') = '' THEN  null  ELSE ', '||substr(ds_bairro,1,38) END , 
	CASE WHEN coalesce(ds_municipio::text, '') = '' THEN  null  ELSE ', '||substr(ds_municipio,1,38) END , 
	CASE WHEN coalesce(sg_estado::text, '') = '' THEN  null  ELSE ', '||substr(sg_estado,1,2) END  
into STRICT	ds_endereco_w, 
	nr_endereco_w, 
	ds_complemento_w, 
	ds_bairro_w, 
	ds_municipio_w, 
	sg_estado_w 
from	pessoa_juridica_compl 
where	cd_cgc 		= cd_cgc_p 
and	nr_sequencia = nr_seq_compl_pj;
 
ds_retorno_w	:= ds_endereco_w || nr_endereco_w || ds_complemento_w || ds_bairro_w || ds_municipio_w || sg_estado_w;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
