CREATE OR REPLACE FUNCTION pls_obter_end_compl_tel_adic ( cd_pessoa_fisica_p text, nr_seq_tel_aidc_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_endereco_w		varchar(4000);
nr_endereco_w		varchar(4000);
ds_complemento_w	varchar(4000);
ds_bairro_w		varchar(4000);
sg_estado_w		varchar(4000);
ds_municipio_w		varchar(4000);


BEGIN

select	ds_endereco,
	CASE WHEN coalesce(nr_endereco::text, '') = '' THEN  null  ELSE ', '||nr_endereco END ,
	CASE WHEN coalesce(ds_complemento::text, '') = '' THEN  null  ELSE ', '||ds_complemento END ,
	CASE WHEN coalesce(ds_bairro::text, '') = '' THEN  null  ELSE ', '||ds_bairro END ,
	CASE WHEN coalesce(sg_estado::text, '') = '' THEN  null  ELSE ', '||sg_estado END ,
	CASE WHEN coalesce(ds_municipio::text, '') = '' THEN  null  ELSE ', '||ds_municipio END
into STRICT	ds_endereco_w,
	nr_endereco_w,
	ds_complemento_w,
	ds_bairro_w,
	sg_estado_w,
	ds_municipio_w
from	compl_pf_tel_adic
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	nr_sequencia		= nr_seq_tel_aidc_p;

ds_retorno_w	:= substr(ds_endereco_w || nr_endereco_w || ds_complemento_w || ds_bairro_w || ds_municipio_w || sg_estado_w, 1, 4000);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
