CREATE OR REPLACE FUNCTION pls_obter_end_corresp_pf_pj ( cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(500);
ie_tipo_complemento_w	smallint;


BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	select	max(ie_endereco_correspondencia) 
	into STRICT	ie_tipo_complemento_w 
	from	pessoa_fisica 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	 
	select	upper(ds_bairro || ' - ' || obter_desc_municipio_ibge(cd_municipio_ibge) || ' - ' || ds_complemento || ' - CEP:' || cd_cep) || ' - ' || ds_email || ' - ' || nr_ddi_telefone || '(' || nr_ddd_telefone || ')' || nr_telefone 
	into STRICT	ds_retorno_w 
	from	compl_pessoa_fisica 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	ie_tipo_complemento = ie_tipo_complemento_w;
end if;
 
if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then 
	select	max(ie_endereco_correspondencia) 
	into STRICT	ie_tipo_complemento_w 
	from	pessoa_juridica_estab 
	where	cd_cgc = cd_cgc_p;
	 
	select	upper(ds_bairro || ' - ' || obter_desc_municipio_ibge(cd_municipio_ibge) || ' - ' || ds_complemento || ' - CEP:' || cd_cep) || ' - ' || ds_email || ' - ' || nr_telefone 
	into STRICT	ds_retorno_w 
	from	pessoa_juridica_compl 
	where	ie_tipo_complemento = ie_tipo_complemento_w 
	and	cd_cgc = cd_cgc_p;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
