CREATE OR REPLACE FUNCTION pls_obter_end_pagador ( nr_seq_pagador_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);	
cd_cgc_w			varchar(14);
cd_cgc_filial_w			varchar(14);
cd_pessoa_fisica_w		varchar(10);	
nr_seq_destino_corresp_w	bigint;
nr_seq_mensalidade_w		bigint;
			

BEGIN 
select	max(a.nr_seq_destino_corresp), 
	max(a.cd_cgc), 
	max(a.cd_pessoa_fisica) 
into STRICT	nr_seq_destino_corresp_w, 
	cd_cgc_w, 
	cd_pessoa_fisica_w 
from	pls_contrato_pagador a 
where	a.nr_sequencia = nr_seq_pagador_p;
 
select	max(a.nr_sequencia) 
into STRICT	nr_seq_mensalidade_w 
from	pls_mensalidade a 
where	a.nr_seq_pagador = nr_seq_pagador_p;
 
if (nr_seq_destino_corresp_w IS NOT NULL AND nr_seq_destino_corresp_w::text <> '') then 
	select	max(a.cd_cgc_filial) 
	into STRICT	cd_cgc_filial_w 
	from	pls_destino_corresp a 
	where	a.nr_sequencia	= nr_seq_destino_corresp_w;
	 
	if (cd_cgc_filial_w IS NOT NULL AND cd_cgc_filial_w::text <> '') then 
		ds_retorno_w := obter_dados_pf_pj(null,cd_cgc_filial_w,ie_opcao_p);	
	end if;	
else 
	ds_retorno_w := pls_obter_compl_pagador(nr_seq_pagador_p,ie_opcao_p);
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
