CREATE OR REPLACE FUNCTION pls_obter_entrevista_benef ( nr_seq_pessoa_proposta_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_proposta_w		bigint;
cd_pessoa_fisica_w		varchar(10);
nr_seq_entrevista_w		bigint;


BEGIN

/* Obter dados da pessoa da proposta */

select	nr_seq_proposta,
	cd_beneficiario
into STRICT	nr_seq_proposta_w,
	cd_pessoa_fisica_w
from	pls_proposta_beneficiario
where	nr_sequencia	= nr_seq_pessoa_proposta_p;

/* Obter o n√∫mero da entrevista qualificada */

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_entrevista_w
from	pls_entrevista_qualificada
where	nr_seq_proposta		= nr_seq_proposta_w
and	cd_pessoa_fisica	= cd_pessoa_fisica_w;

return	nr_seq_entrevista_w;

end;
