SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_erro_intercambio ( nr_seq_inconsistencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
Opções
D - Descrição
C - Código
CD -  Código e descrição
*/
ds_retorno_w		varchar(255);
ds_mensagem_w		varchar(255);
cd_incosistencia_w	smallint;


BEGIN

select	ds_inconsistencia,
	cd_inconsistencia
into STRICT	ds_mensagem_w,
	cd_incosistencia_w
from	ptu_inconsistencia
where	nr_sequencia = nr_seq_inconsistencia_p;

if (ie_opcao_p = 'D') then
	ds_retorno_w :=	ds_mensagem_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w := to_char(cd_incosistencia_w);
elsif (ie_opcao_p	= 'CD') then
	ds_retorno_w	:= cd_incosistencia_w||' - '||ds_mensagem_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
