SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_espacamento_html (ds_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_espaco_w		varchar(255);

/*
PARA QUE ESSA ROTINA FUNCIONE CORRETAMENTE PARA APLICAR ESPAÇO EM GRID NO HTML, NO COMPONENTE
QUE SE ESTIVER TRABALHANDO DEVE-SE APLICAR A SEGUINTE DIRETIVA VIA CÓDIGO FONTE:

onLoadColumnsDef() {
    // so the grid accepts html tags in the text
    this.handler.getGridHandler().setAllowHtmlContent("NOME_DA_COLUNA_DO_GRID", true);
  }

*/
BEGIN


if (coalesce(ds_tipo_p, 'X') <> 'X') then
	begin


	for i in 1..Length(ds_tipo_p) loop
		begin
		if (substr(ds_tipo_p, i, 1) = ' ') then

			ds_espaco_w	:= substr(ds_espaco_w || chr(38) || 'emsp;', 1, 255);
		end if;
		end;
	end loop;

	ds_retorno_w	:= substr(ds_espaco_w || ds_tipo_p, 1, 255);
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_espacamento_html (ds_tipo_p text) FROM PUBLIC;

