SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_especialidade_ptu ( nr_seq_proc_mat_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_especialidade_w		varchar(255);
cd_especialidade_w		smallint;
qt_registro_w			bigint := 0;


BEGIN
if	((ie_tipo_item_p	= 'P')	or (ie_tipo_item_p		= 'R')) then
	select	max(cd_especialidade)
	into STRICT	cd_especialidade_w
	from	ptu_nota_servico
	where	nr_seq_conta_proc	= nr_seq_proc_mat_p;
elsif (ie_tipo_item_p	= 'M') then
	select	max(cd_especialidade)
	into STRICT	cd_especialidade_w
	from	ptu_nota_servico
	where	nr_seq_conta_mat	= nr_seq_proc_mat_p;
end if;

if (cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	especialidade_medica
	where	cd_ptu = cd_especialidade_w;

	select	max(substr(cd_especialidade_w || ' - ' || ds_especialidade,1,255))
	into STRICT	ds_especialidade_w
	from	especialidade_medica
	where	cd_ptu		= cd_especialidade_w;
end if;

/* Se houver mais de uma especialidade com mesmo código PTU, então informar aquele que esta com o IE_PTU_A500 marcado,
     senão continua buscando pelo MAX da descrição da especialidade. */
if (qt_registro_w > 1) then
	select	coalesce(max(substr(cd_especialidade_w || ' - ' || ds_especialidade,1,255)),ds_especialidade_w)
	into STRICT	ds_especialidade_w
	from	especialidade_medica
	where	cd_ptu		= cd_especialidade_w
	and	ie_ptu_a500	= 'S';
end if;

return	ds_especialidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_especialidade_ptu ( nr_seq_proc_mat_p bigint, ie_tipo_item_p text) FROM PUBLIC;

