CREATE OR REPLACE FUNCTION pls_obter_espec_prest_serv_hab ( nr_seq_prestador_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


cd_especialidade_w		varchar(4000)	:= null;
ds_retorno_w			varchar(4000);

C01 CURSOR FOR
	SELECT	distinct
		a.cd_especialidade
	from	pls_prestador_med_espec	a
	where	a.nr_seq_prestador	= nr_seq_prestador_p
	and	clock_timestamp() between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp()), 'dd') and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()))
	order by
		cd_especialidade;

C02 CURSOR FOR
	SELECT	distinct
		a.cd_especialidade
	from	pls_prestador_med_espec	a
	where	a.cd_pessoa_fisica	= cd_medico_p
	and	a.nr_seq_prestador	= nr_seq_prestador_p
	and	clock_timestamp() between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp()), 'dd') and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()))
	order by
		cd_especialidade;
BEGIN

if (coalesce(cd_medico_p::text, '') = '') then
	for r_c01_w in C01() loop
		begin
		if (coalesce(ds_retorno_w, 'X') not like('%' || r_c01_w.cd_especialidade || '%' )) then
			ds_retorno_w	:= ds_retorno_w || r_c01_w.cd_especialidade || ',';
		end if;
		end;
	end loop;
else
	for r_c02_w in C02() loop
		begin
		if (coalesce(ds_retorno_w, 'X') not like('%' || r_c02_w.cd_especialidade || '%' )) then
			ds_retorno_w	:= ds_retorno_w || r_c02_w.cd_especialidade || ',';
		end if;
		end;
	end loop;
end if;

ds_retorno_w	:= substr(ds_retorno_w, 1, length(ds_retorno_w) - 1);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
