SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_espec_profissional ( nr_sequencia_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter as especialidades do profissional
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------

Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(4000);
ds_especialidade_w		varchar(255)	:= '';
cd_especialidade_w		bigint	:= null;

C01 CURSOR FOR
	SELECT	distinct b.cd_especialidade,
		b.ds_especialidade
	from	pls_prestador c,
		especialidade_medica	b,
		pls_prestador_med_espec	a
	where	b.cd_especialidade	= a.cd_especialidade
	and 	((a.cd_pessoa_fisica 	= c.cd_pessoa_fisica) or (coalesce(a.cd_pessoa_fisica::text, '') = '' and coalesce(c.cd_pessoa_fisica,'X') = 'X'))
	and     a.nr_seq_prestador	= c.nr_sequencia
	and	a.nr_seq_prestador	= nr_sequencia_p
	and	clock_timestamp() between trunc(coalesce(a.dt_inicio_vigencia, clock_timestamp())) and fim_dia(coalesce(a.dt_fim_vigencia, clock_timestamp()))
	order by b.ds_especialidade desc;


BEGIN
open C01;
loop
fetch C01 into	
	cd_especialidade_w,
	ds_especialidade_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w	:= ds_retorno_w || ', ' || ds_especialidade_w;
	end;
end loop;
close C01;

if (substr(ds_retorno_w, 1, 2) = ', ') then
	ds_retorno_w	:= substr(ds_retorno_w, 2, length(ds_retorno_w));
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_espec_profissional ( nr_sequencia_p text) FROM PUBLIC;

