BEGIN 
if (nr_versao_ptu_p IS NOT NULL AND nr_versao_ptu_p::text <> '') then 
	select	max(cd_ptu) 
	into STRICT	cd_retorno_w 
	from	espec_med_ptu_tit_cert 
	where	cd_especialidade	= cd_especialidade_p 
	and (nr_versao_ptu		= nr_versao_ptu_p or coalesce(nr_versao_ptu::text, '') = '');
else 
	select	max(nr_versao_ptu) 
	into STRICT	nr_versao_ptu_w 
	from	espec_med_ptu_tit_cert 
	where	cd_especialidade	= cd_especialidade_p;
	 
	if (nr_versao_ptu_w IS NOT NULL AND nr_versao_ptu_w::text <> '') then 
		select	max(cd_ptu) 
		into STRICT	cd_retorno_w 
		from	espec_med_ptu_tit_cert 
		where	cd_especialidade	= cd_especialidade_p 
		and	nr_versao_ptu		= nr_versao_ptu_w;
	else 
		select	max(cd_ptu) 
		into STRICT	cd_retorno_w 
		from	espec_med_ptu_tit_cert 
		where	cd_especialidade	= cd_especialidade_p;
	end if;
end if;
	 
return	cd_retorno_w;
 
end;
