CREATE OR REPLACE FUNCTION pls_obter_estab_ans ( cd_ans_p pls_outorgante.cd_ans%type) RETURNS varchar AS $body$
DECLARE


/*
 * Retorna o CD_ESTABELECIMENTO a partir do registro ANS passado por par√¢metro.
 */
ds_retorno_w	estabelecimento.cd_estabelecimento%type;


BEGIN

begin
	select	min(a.cd_estabelecimento)
	into STRICT	ds_retorno_w
	from	pls_outorgante a,
		estabelecimento b
	where	b.cd_estabelecimento = a.cd_estabelecimento
	and	a.cd_ans = cd_ans_p;
exception
when others then
	ds_retorno_w	:= pls_parametro_operadora_web('EST');
end;

if (coalesce(ds_retorno_w::text, '') = '') then
	ds_retorno_w	:= pls_parametro_operadora_web('EST');
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
