SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_estado_congenere ( nr_seq_congenere_p pls_congenere.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS PESSOA_JURIDICA.SG_ESTADO%TYPE AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter a sigla do estado da Operadora congenere conforme sequência passada por parâmetro
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Esta function é utilizada na geração das Ocorrências, favor cuidar se for alterar para utilizar em outro lugar.

Alterações:
------------------------------------------------------------------------------------------------------------------
jjung OS 597885 - 04/05/2013 - 	Criação da function para obter o estado da congenere na
			PLS_CONTA_INTERCAMBIO_V que é usada pelos filtros de
			intercâmbio da ocorrência;
------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
sg_estado_w	pessoa_juridica.sg_estado%type;
cd_cgc_w	pessoa_juridica.cd_cgc%type;


BEGIN

if (nr_seq_congenere_p IS NOT NULL AND nr_seq_congenere_p::text <> '') then

	-- Obter o código da congenere
	select	max(a.cd_cgc)
	into STRICT	cd_cgc_w
	from	pls_congenere a
	where	a.nr_sequencia		= nr_seq_congenere_p
	and	a.cd_estabelecimento	= cd_estabelecimento_p;

	-- Só buscar a informação do estado caso encontrar o dado da pessoa_juridica
	if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then

		-- Obter o estador da congenere
		select	max(a.sg_estado)
		into STRICT	sg_estado_w
		from	pessoa_juridica a
		where	a.cd_cgc		= cd_cgc_w;
	end if;
end if;

return	sg_estado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_estado_congenere ( nr_seq_congenere_p pls_congenere.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;

