SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_estagio_agenda ( dt_analise_p pls_analise_adesao.dt_analise%type, cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type) RETURNS varchar AS $body$
DECLARE


/*
Retorno:
EM	Entrevista marcada
FE	Faltou na entrevista
EP	Marcacao da entrevista pendente
ER	Entrevista realizada
*/
ie_status_agenda_w		agenda_consulta.ie_status_agenda%type;
nr_seq_agenda_consulta_w	agenda_consulta.nr_sequencia%type;
ds_retorno_w			varchar(2);


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_agenda_consulta_w
from	agenda_consulta
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	dt_agenda >= trunc(dt_analise_p,'dd')
and	ie_status_agenda <> 'C';

if (coalesce(nr_seq_agenda_consulta_w::text, '') = '') then
	ds_retorno_w	:= 'EP';	-- Marcacao da entrevista pendente
else
	select	coalesce(ie_status_agenda, 'X')
	into STRICT	ie_status_agenda_w
	from	agenda_consulta
	where	nr_sequencia = nr_seq_agenda_consulta_w;

	if (ie_status_agenda_w = 'E') then
		ds_retorno_w	:= 'ER';	-- Entrevista realizada
	else
		select	max(nr_sequencia)
		into STRICT	nr_seq_agenda_consulta_w
		from	agenda_consulta
		where	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	dt_agenda >= clock_timestamp()
		and	ie_status_agenda <> 'C';
		
		if (nr_seq_agenda_consulta_w IS NOT NULL AND nr_seq_agenda_consulta_w::text <> '') then
			ds_retorno_w	:= 'EM';	-- Entrevista marcada
		else
			ds_retorno_w	:= 'FE';	-- Faltou na entrevista
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
