CREATE OR REPLACE FUNCTION pls_obter_estagio_carteira ( nr_seq_seg_carteira_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(250);
ds_estagio_w		varchar(250);
ie_estagio_w		varchar(10);

/*
D - Descrição do estágio
C - Código do estágio
*/
BEGIN

select	substr(obter_valor_dominio(3351,ie_estagio_emissao),1,255),
	ie_estagio_emissao
into STRICT	ds_estagio_w,
	ie_estagio_w
from	pls_segurado_cart_estagio
where	nr_seq_cartao_seg	= nr_seq_seg_carteira_p
and	dt_alteracao	= (	SELECT	max(dt_alteracao)
				from	pls_segurado_cart_estagio
				where	nr_seq_cartao_seg	= nr_seq_seg_carteira_p);

if (ie_opcao_p = 'D') then
	ds_retorno_w := ds_estagio_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w := ie_estagio_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
