CREATE OR REPLACE FUNCTION pls_obter_estipulante_benef ( nr_seq_segurado_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);
nm_estipulante_contrato_w	varchar(255);
nm_estipulante_intercambio_w	varchar(255);


BEGIN 
 
select	substr(pls_obter_dados_segurado(nr_sequencia,'ES'),1,255), 
	substr(pls_obter_dados_intercambio(nr_seq_intercambio,'N'),1,255) 
into STRICT	nm_estipulante_contrato_w, 
	nm_estipulante_intercambio_w 
from	pls_segurado	 
where	nr_sequencia = nr_seq_segurado_p;
 
if (nm_estipulante_contrato_w IS NOT NULL AND nm_estipulante_contrato_w::text <> '') then 
	ds_retorno_w := nm_estipulante_contrato_w;
elsif (nm_estipulante_intercambio_w IS NOT NULL AND nm_estipulante_intercambio_w::text <> '') then 
	ds_retorno_w := nm_estipulante_intercambio_w;
else 
	ds_retorno_w := null;
end if;
	 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
