CREATE OR REPLACE FUNCTION pls_obter_estipulante_contrato ( nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w			varchar(255);


BEGIN 
 
begin 
select	substr(obter_nome_pf_pj(a.cd_pf_estipulante, a.cd_cgc_estipulante),1,200) 
into STRICT	ds_retorno_w 
from	pls_contrato		a 
where	a.nr_sequencia		= nr_seq_contrato_p;
 
exception 
when others then 
	ds_retorno_w		:= '';
end;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
