SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_estip_contr_web ( nr_seq_congenere_p bigint, nr_seq_intercambio_p bigint, nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE

		 
nm_estipulante_w	varchar(255);


BEGIN 
 
if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then 
	select	substr(obter_nome_pf_pj(cd_pf_estipulante, cd_cgc_estipulante),1,200) 
	into STRICT	nm_estipulante_w 
	from	pls_contrato 
	where	nr_Sequencia = nr_seq_contrato_p;
elsif (nr_seq_intercambio_p IS NOT NULL AND nr_seq_intercambio_p::text <> '') then 
	select 	substr(obter_nome_pf_pj(cd_pessoa_fisica, cd_cgc),1,200) 
	into STRICT	nm_estipulante_w 
	from	pls_intercambio 
	where	nr_sequencia	= nr_seq_intercambio_p;
elsif (nr_seq_congenere_p IS NOT NULL AND nr_seq_congenere_p::text <> '') then 
	select	max(a.ds_razao_social) 
	into STRICT	nm_estipulante_w 
	from	pessoa_juridica	a, 
		pls_congenere	b 
	where	b.cd_cgc	= a.cd_cgc 
	and	b.nr_sequencia	= nr_seq_congenere_p;
end if;
 
return	nm_estipulante_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_estip_contr_web ( nr_seq_congenere_p bigint, nr_seq_intercambio_p bigint, nr_seq_contrato_p bigint) FROM PUBLIC;

