CREATE OR REPLACE FUNCTION pls_obter_etapa_cobranca_notif ( nr_seq_notific_item_p pls_notificacao_item.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_etapa_w			etapa_cobranca.ds_etapa%type;
nr_seq_cobranca_w		cobranca.nr_sequencia%type;


BEGIN

ds_etapa_w	:= null;

select	max(nr_sequencia)
into STRICT	nr_seq_cobranca_w
from	cobranca
where	nr_seq_notific_item = nr_seq_notific_item_p;

if (nr_seq_cobranca_w IS NOT NULL AND nr_seq_cobranca_w::text <> '') then
	select	substr(obter_desc_etapa_cobranca(obter_etapa_cobranca(nr_seq_cobranca_w)),1,255)
	into STRICT	ds_etapa_w
	;
end if;

return	ds_etapa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
