SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_excecao_conv_pac ( nr_seq_regra_p bigint, nr_seq_cooperativa_p bigint, -- cooperativa segurado
 nr_seq_congenere_p bigint) RETURNS varchar AS $body$
 -- cooperativa do intercambio
DECLARE


ie_excecao_regra_w	varchar(1)	:= 'N';
sg_estado_seg_w		pessoa_juridica.sg_estado%type;
sg_estado_int_w		pessoa_juridica.sg_estado%type;


C01 CURSOR FOR
	SELECT	ie_tipo_intercambio
	from	pls_conv_pacote_excecao
	where	nr_seq_regra_conversao	= nr_seq_regra_p
	and	ie_situacao 		= 'A';

BEGIN
/*Obter estado coop.segurado*/

begin
select	sg_estado
into STRICT	sg_estado_seg_w
from	pls_congenere	a,
	pessoa_juridica b
where	a.cd_cgc	= b.cd_cgc
and	a.nr_sequencia	= nr_seq_cooperativa_p;
exception
when others then
	sg_estado_seg_w	:= null;
end;

/*Obter estado coop.intercambio*/

begin
select	sg_estado
into STRICT	sg_estado_int_w
from	pls_congenere	a,
	pessoa_juridica b
where	a.cd_cgc	= b.cd_cgc
and	a.nr_sequencia	= nr_seq_congenere_p;
exception
when others then
	sg_estado_int_w	:= null;
end;

for r_C01 in C01 loop


	if (r_C01.ie_tipo_intercambio = 'E')	then
			if (sg_estado_seg_w = sg_estado_int_w)	then
			ie_excecao_regra_w	:= 'S';
		end if;

	elsif (r_C01.ie_tipo_intercambio = 'N')	and (sg_estado_seg_w <> sg_estado_int_w)	then
		ie_excecao_regra_w	:= 'S';
	end if;
end loop;

return	ie_excecao_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_excecao_conv_pac ( nr_seq_regra_p bigint, nr_seq_cooperativa_p bigint, nr_seq_congenere_p bigint) FROM PUBLIC;

