CREATE OR REPLACE FUNCTION pls_obter_excec_lib_guia_inter ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_area_procedimento_p bigint, cd_grupo_proc_p bigint, cd_especialidade_p bigint, dt_solicitacao_p timestamp, ie_tipo_contrato_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_regra_excecao_w		bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_regra_excecao_w
from	pls_excecao_lib_inter
where	((coalesce(cd_procedimento::text, '') = '')	or (cd_procedimento		= cd_procedimento_p))
and	((coalesce(ie_origem_proced::text, '') = '')	or (ie_origem_proced		= ie_origem_proced_p))
and	((coalesce(cd_grupo_proc::text, '') = '')	or (cd_grupo_proc		= cd_grupo_proc_p))
and	((coalesce(cd_especialidade::text, '') = '')	or (cd_especialidade		= cd_especialidade_p))
and	((coalesce(cd_area_procedimento::text, '') = '')	or (cd_area_procedimento	= cd_area_procedimento_p))
and	((coalesce(ie_tipo_contrato::text, '') = '')	or (ie_tipo_contrato		= ie_tipo_contrato_p))
and	trunc(dt_solicitacao_p)	between trunc(dt_inicio_vigencia)		and trunc(fim_dia(coalesce(dt_fim_vigencia,clock_timestamp())))
order by cd_area_procedimento,
	 cd_especialidade,
	 cd_grupo_proc,
	 cd_procedimento;

return	nr_seq_regra_excecao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
