CREATE OR REPLACE FUNCTION pls_obter_exibir_guia_med ( cd_pessoa_fisica_p text, cd_cgc_p text, nr_seq_ptu_prestador_p text, cd_param_p bigint, ie_guia_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(80);	
ie_guia_medico_w	varchar(20);


BEGIN 
ds_retorno_w := '';
ie_guia_medico_w := '';
 
if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then 
	select	b.ie_guia_medico 
	into STRICT 	ie_guia_medico_w 
	from	pls_cooperado_ausencia b, 
		pls_cooperado c	 
	where	b.nr_seq_cooperado	= c.nr_sequencia 
	and	((clock_timestamp() between b.dt_inicio and b.dt_fim) 
	or (coalesce(b.dt_fim::text, '') = '' and clock_timestamp() >= b.dt_inicio)) 
	and	c.cd_cgc = cd_cgc_p;
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	select	b.ie_guia_medico 
	into STRICT 	ie_guia_medico_w 
	from	pls_cooperado_ausencia b, 
		pls_cooperado c	 
	where	b.nr_seq_cooperado	= c.nr_sequencia 
	and	((clock_timestamp() between b.dt_inicio and b.dt_fim) 
	or (coalesce(b.dt_fim::text, '') = '' and clock_timestamp() >= b.dt_inicio)) 
	and	c.cd_pessoa_fisica	= cd_pessoa_fisica_p;
end if;
 
if (coalesce(ie_guia_medico_w,'S') = 'S') then 
	ds_retorno_w	:= obter_ptu_especialidade_rce(nr_seq_ptu_prestador_p, cd_param_p, ie_guia_p);
elsif (coalesce(ie_guia_medico_w,'S') = 'N') then 
	ds_retorno_w	:= 'N';
end if;	
	 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
