CREATE OR REPLACE FUNCTION pls_obter_existe_benef ( cd_usuario_plano_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w			bigint;


BEGIN
	select	max(a.nr_sequencia)
	into STRICT	nr_retorno_w
	from	pls_segurado a,
		pls_segurado_carteira b
	where	a.nr_sequencia		= b.nr_seq_segurado
	and	b.cd_estabelecimento	= cd_estabelecimento_p
	and (b.cd_usuario_plano	= cd_usuario_plano_p
	or	b.nr_cartao_intercambio	= cd_usuario_plano_p);

 	if (coalesce(nr_retorno_w::text, '') = '') then
		select	coalesce(max(a.nr_sequencia),0)
		into STRICT	nr_retorno_w
		from	pls_segurado a,
		pls_segurado_carteira b
		where	a.nr_sequencia		= b.nr_seq_segurado
		and (b.cd_usuario_plano	= cd_usuario_plano_p
		or	b.nr_cartao_intercambio	= cd_usuario_plano_p);

	end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
