CREATE OR REPLACE FUNCTION pls_obter_existe_data_guia ( dt_inicial_p timestamp, dt_final_p timestamp, ie_tipo_data_p bigint, nr_seq_guia_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint := 0;


BEGIN

if (ie_tipo_data_p = 0) then
	select  1
	into STRICT	ds_retorno_w
	from	pls_guia_plano a
	where	nr_sequencia = nr_seq_guia_p
	and	dt_solicitacao between dt_inicial_p and fim_dia(dt_final_p);
elsif (ie_tipo_data_p = 1) then
	select  1
	into STRICT	ds_retorno_w
	from	pls_guia_plano a
	where	nr_sequencia = nr_seq_guia_p
	and	dt_autorizacao between dt_inicial_p and fim_dia(dt_final_p);
elsif (ie_tipo_data_p = 2) then
	ds_retorno_w := 1;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
