CREATE OR REPLACE FUNCTION pls_obter_existe_guia_ref_web ( cd_guia_ref_p text, nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Obter se a guia referência existe na função OPS - Autorizações
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  x ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_existe_guia_ref_w		varchar(1);


BEGIN
if (cd_guia_ref_p IS NOT NULL AND cd_guia_ref_p::text <> '' AND nr_seq_segurado_p IS NOT NULL AND nr_seq_segurado_p::text <> '') then
	begin
		select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_existe_guia_ref_w
		from	pls_guia_plano
		where	cd_guia		= cd_guia_ref_p
		and	nr_seq_segurado	= nr_seq_segurado_p;
	exception
	when others then
		ie_existe_guia_ref_w	:= 'N';
	end;
end if;

return	ie_existe_guia_ref_w;

end;
$body$
