CREATE OR REPLACE FUNCTION pls_obter_existe_item_resumo ( nr_seq_conta_proc_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';
ie_tipo_guia_w		varchar(2);
nr_seq_proc_ref_w	bigint;


BEGIN

select	max(nr_seq_proc_ref)
into STRICT	nr_seq_proc_ref_w
from	pls_conta_proc
where	nr_sequencia = nr_seq_conta_proc_p;

if (coalesce(nr_seq_proc_ref_w,0) > 0) then
	select	max(a.ie_tipo_guia)
	into STRICT	ie_tipo_guia_w
	from	pls_conta_proc	b,
		pls_conta	a
	where	a.nr_sequencia	= b.nr_seq_conta
	and	b.nr_sequencia	= nr_seq_proc_ref_w;

	if (ie_tipo_guia_w = '5') then
		ds_retorno_w := 'S';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
