CREATE OR REPLACE FUNCTION pls_obter_existe_pgto_escrit (nr_lote_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);


BEGIN

begin
select	'S'
into STRICT	ds_retorno_w

where	exists (SELECT	nr_sequencia
		from	banco_escritural
		where	nr_seq_lote_pag_prest = nr_lote_p);
exception
when others then
	ds_retorno_w := 'N';
end;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
