CREATE OR REPLACE FUNCTION pls_obter_faces_dente_proc (nr_seq_conta_proc_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter os valores de domínio em sequência das faces do dente afetadas no procedimento
odontológico
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_faces_w	tiss_face_dente.cd_face%type	:= null;

c_faces CURSOR(nr_seq_conta_proc_pc	bigint) FOR
	SELECT	a.cd_face_dente
	from	pls_conta_proc_face_dente a
	where	a.nr_seq_conta_proc = nr_seq_conta_proc_pc;

BEGIN
if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
	for r_c_faces in c_faces(nr_seq_conta_proc_p) loop
		ds_faces_w	:= ds_faces_w || r_c_faces.cd_face_dente;
	end loop;
end if;

return	ds_faces_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
