CREATE OR REPLACE FUNCTION pls_obter_faixa_etaria_benef (nr_seq_segurado_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



/*	'D' - Descritivo
	'P' - Preço
	'I' - Idade		*/
qt_idade_inicial_w	integer;
qt_idade_final_w	integer;
vl_preco_atual_w	double precision;
qt_idade_w		integer;
nr_seq_tabela_w		bigint;
ds_retorno_w		varchar(255)	:= '';
dt_nascimento_w		timestamp;
ie_mes_cobranca_reaj_reg_w	varchar(255);


BEGIN

select	a.nr_seq_tabela,
	b.dt_nascimento
into STRICT	nr_seq_tabela_w,
	dt_nascimento_w
from	pessoa_fisica	b,
	pls_segurado	a
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
and	a.nr_sequencia		= nr_seq_segurado_p;

select	coalesce(max(IE_MES_COBRANCA_REAJ_REG),'M')
into STRICT	ie_mes_cobranca_reaj_reg_w
from	PLS_PARAMETROS;

if (ie_mes_cobranca_reaj_reg_w = 'M') then
		qt_idade_w	:= trunc(months_between(clock_timestamp(), trunc(dt_nascimento_w,'month')) / 12);
else
	qt_idade_w	:= trunc(months_between(clock_timestamp(), trunc(add_months(dt_nascimento_w,1),'month')) / 12);
end if;

if (qt_idade_w <> 999) then
	select	max(qt_idade_inicial),
		max(qt_idade_final),
		max(vl_preco_atual)
	into STRICT	qt_idade_inicial_w,
		qt_idade_final_w,
		vl_preco_atual_w
	from	pls_plano_preco
	where	nr_seq_tabela	= nr_seq_tabela_w
	and	qt_idade_w	>= qt_idade_inicial
	and	qt_idade_w	<= qt_idade_final;
end if;

if (qt_idade_w <> 999) then
	if (ie_opcao_p = 'D') then
		ds_retorno_w	:= qt_idade_inicial_w || ' à ' || qt_idade_final_w;
	elsif (ie_opcao_p = 'P') then
		ds_retorno_w	:= to_char(vl_preco_atual_w);
	elsif (ie_opcao_p = 'I') then
		ds_retorno_w	:= to_char(qt_idade_w);
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
