CREATE OR REPLACE FUNCTION pls_obter_faixa_etaria_padrao ( nr_seq_segurado_p bigint, ie_opcao_p text, ie_tipo_faixa_etaria_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


/* 	IE_OPCAO_P
	D = Descrição tipo faixa etária
	I = Idade faixa etária
	S = Sequencia
*/
/*	IE_TIPO_FAIXA_ETARIA_P
	IG = Indicador de Gestão
	C = Contrato
	R = Resultado da operadora
*/
cd_pessoa_fisica_w		varchar(10);
nr_seq_faixa_etaria_w		bigint;
qt_idade_w			integer;
nr_seq_idade_w			bigint;
ds_faixa_etaria_w		varchar(255);
ds_retorno_w			varchar(255)	:= '';
qt_idade_inicial_w		smallint;
qt_idade_final_w		smallint;
cd_estabelecimento_w		integer;


BEGIN

select	a.cd_pessoa_fisica,
	a.cd_estabelecimento
into STRICT	cd_pessoa_fisica_w,
	cd_estabelecimento_w
from	pls_segurado a
where	a.nr_sequencia	= nr_seq_segurado_p;

/*Diego OS 274512 - Alterei pois existe a possibilidade de não haver o estabelecimento no segurado.
		Também verifquei com o Ricardo que o correto seria passar o estabelecimento por parâmetro para que
		seja obtido as regras do estabelecimento que esta sendo usado a function.*/
begin
select	max(nr_sequencia),
	max(ds_faixa_etaria)
into STRICT	nr_seq_faixa_etaria_w,
	ds_faixa_etaria_w
from	pls_faixa_etaria
where	cd_estabelecimento	= cd_estabelecimento_p
and	ie_tipo_faixa_etaria	= ie_tipo_faixa_etaria_p
and	ie_situacao		= 'A';
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 223593, null );
	/* Não existe um o tipo de faixa etária selecionada nos cadastros gerais. Verifique! */

end;

select	trunc(months_between(clock_timestamp(), trunc(dt_nascimento,'month')) / 12)
into STRICT	qt_idade_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_w;

if (qt_idade_w <> 999) then
	select	nr_sequencia,
		qt_idade_inicial,
		qt_idade_final
	into STRICT	nr_seq_idade_w,
		qt_idade_inicial_w,
		qt_idade_final_w
	from	pls_faixa_etaria_item
	where	nr_seq_faixa_etaria = nr_seq_faixa_etaria_w
	and	qt_idade_w	>= qt_idade_inicial
	and	qt_idade_w	<= qt_idade_final;

	if (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_faixa_etaria_w;
	elsif (ie_opcao_p = 'I') then
		ds_retorno_w	:= to_char(qt_idade_inicial_w) || ' à ' || to_char(qt_idade_final_w);
	elsif (ie_opcao_p = 'S') then
		ds_retorno_w	:= to_char(nr_seq_idade_w);
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_faixa_etaria_padrao ( nr_seq_segurado_p bigint, ie_opcao_p text, ie_tipo_faixa_etaria_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

