SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_faixa_etaria_prop ( nr_seq_proposta_benef_p bigint, nr_seq_faixa_etaria_p bigint) RETURNS varchar AS $body$
DECLARE


qt_idade_inicial_w	integer;
qt_idade_final_w	integer;
vl_preco_atual_w	double precision;
ds_retorno_w		varchar(255)	:= '';
tx_desconto_w		double precision := 0;
nr_seq_regra_desconto_w	bigint;
cd_estabelecimento_w	integer;
nr_seq_proposta_w	bigint;
dt_inicio_proposta_w	pls_proposta_adesao.dt_inicio_proposta%type;
vl_antes_reajuste_w	pls_reajuste_preco.vl_base%type;


BEGIN

select	max(nr_seq_proposta)
into STRICT	nr_seq_proposta_w
from	pls_proposta_beneficiario
where	nr_sequencia	= nr_seq_proposta_benef_p;

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_w;

select	max(dt_inicio_proposta)
into STRICT	dt_inicio_proposta_w
from	pls_proposta_adesao
where	nr_sequencia	= nr_seq_proposta_w;

SELECT * FROM pls_obter_regra_desconto(nr_seq_proposta_benef_p, 2, cd_estabelecimento_w, tx_desconto_w, nr_seq_regra_desconto_w) INTO STRICT tx_desconto_w, nr_seq_regra_desconto_w;

select	max(vl_base)
into STRICT	vl_antes_reajuste_w
from	pls_reajuste_preco
where	nr_seq_preco	= nr_seq_faixa_etaria_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	dt_reajuste	= (	SELECT	min(dt_reajuste)
				from	pls_reajuste_preco
				where	nr_seq_preco	= nr_seq_faixa_etaria_p
				and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
				and	dt_reajuste > dt_inicio_proposta_w);

if (vl_antes_reajuste_w IS NOT NULL AND vl_antes_reajuste_w::text <> '') then
	vl_preco_atual_w := vl_antes_reajuste_w;
else
	select	coalesce(vl_preco_atual,0)
	into STRICT	vl_preco_atual_w
	from	pls_plano_preco
	where	nr_sequencia	= nr_seq_faixa_etaria_p;
end if;

vl_preco_atual_w	:= vl_preco_atual_w - dividir((vl_preco_atual_w * tx_desconto_w), 100);
ds_retorno_w		:= to_char(vl_preco_atual_w);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
