CREATE OR REPLACE FUNCTION pls_obter_faixa_salarial ( nr_seq_faixa_salarial_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao:
	D = retorna a faixa etária em si - campo DS_FAIXA_SALARIAL
	N = retorna o nível da faixa salarial
*/
ds_retorno_w	pls_faixa_salarial.ds_faixa_salarial%type;


BEGIN

ds_retorno_w := null;

if (nr_seq_faixa_salarial_p IS NOT NULL AND nr_seq_faixa_salarial_p::text <> '') then
	if (ie_opcao_p = 'D') then
		select	ds_faixa_salarial
		into STRICT	ds_retorno_w
		from	pls_faixa_salarial
		where	nr_sequencia = nr_seq_faixa_salarial_p;
	elsif (ie_opcao_p = 'N') then
		select	nr_nivel
		into STRICT	ds_retorno_w
		from	pls_faixa_salarial
		where	nr_sequencia = nr_seq_faixa_salarial_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
