CREATE OR REPLACE FUNCTION pls_obter_fluxo_pgto ( nr_seq_periodo_p bigint) RETURNS bigint AS $body$
DECLARE

nr_fluxo_pgto_w	bigint;


BEGIN

/*retorna o fluxo de pagamento correspondente ao lote*/

select 	max(nr_fluxo_pgto)
into STRICT 	nr_fluxo_pgto_w
from 	pls_periodo_pagamento
where 	nr_sequencia=nr_seq_periodo_p;

return nr_fluxo_pgto_w;

end;
$body$
