SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_fone_prest_guia ( nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_tel_retorno_w		varchar(255);
ie_tipo_endereco_w		varchar(255);
cd_cgc_w			varchar(255);
cd_pessoa_fisica_w		varchar(255);


BEGIN 
 
begin 
	select	ie_tipo_endereco, 
		cd_cgc, 
		cd_pessoa_fisica 
	into STRICT	ie_tipo_endereco_w, 
		cd_cgc_w, 
		cd_pessoa_fisica_w 
	from	pls_prestador 
	where	nr_sequencia	 = nr_seq_prestador_p;
exception 
when others then 
	ie_tipo_endereco_w	:= 'X';
end;
 
if (coalesce(ie_tipo_endereco_w,'X')	= 'PJ') then 
	select	max('('||nr_ddd_telefone||')'||nr_telefone) 
	into STRICT	nr_tel_retorno_w 
	from	pessoa_juridica 
	where	cd_cgc			= cd_cgc_w;
elsif (coalesce(ie_tipo_endereco_w,'X')	= 'PJA') then 
	select	max('('||nr_ddd_telefone||')'||nr_telefone) 
	into STRICT	nr_tel_retorno_w 
	from	pessoa_juridica_compl 
	where	cd_cgc			= cd_cgc_w 
	and	ie_tipo_complemento	= 6;
elsif (coalesce(ie_tipo_endereco_w,'X')	= 'PJC') then 
	select	max('('||nr_ddd_telefone||')'||nr_telefone) 
	into STRICT	nr_tel_retorno_w 
	from	pessoa_juridica_compl 
	where	cd_cgc			= cd_cgc_w 
	and	ie_tipo_complemento	= 1;
elsif (coalesce(ie_tipo_endereco_w,'X')	= 'PJF') then 
	select	max('('||nr_ddd_telefone||')'||nr_telefone) 
	into STRICT	nr_tel_retorno_w 
	from	pessoa_juridica_compl 
	where	cd_cgc			= cd_cgc_w 
	and	ie_tipo_complemento	= 2;
elsif (coalesce(ie_tipo_endereco_w,'X')	= 'PFC') then 
	select	max('('||nr_ddd_telefone||')'||nr_telefone) 
	into STRICT	nr_tel_retorno_w 
	from	compl_pessoa_fisica a 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w 
	and	ie_tipo_complemento 	= 2;
elsif (coalesce(ie_tipo_endereco_w,'X')	= 'PFR') then 
	select	max('('||nr_ddd_telefone||')'||nr_telefone) 
	into STRICT	nr_tel_retorno_w 
	from	compl_pessoa_fisica a 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w 
	and	ie_tipo_complemento 	= 1;
end if;
 
return	nr_tel_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
