CREATE OR REPLACE FUNCTION pls_obter_fx_etaria_contrato ( qt_idade_p bigint) RETURNS varchar AS $body$
DECLARE



qt_idade_inicial_w	integer;
qt_idade_final_w	integer;
ds_retorno_w		varchar(255)	:= '';
nr_seq_faixa_etaria_w	bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_faixa_etaria_w
from	pls_faixa_etaria
where	ie_tipo_faixa_etaria = 'C'
and	ie_situacao = 'A';

if (nr_seq_faixa_etaria_w > 0) then

	select	qt_idade_inicial,
		qt_idade_final
	into STRICT 	qt_idade_inicial_w,
		qt_idade_final_w
	from	pls_faixa_etaria_item
	where	nr_seq_faixa_etaria = nr_seq_faixa_etaria_w
	and	qt_idade_p between qt_idade_inicial and qt_idade_final;

	ds_retorno_w := qt_idade_inicial_w || ' Ã  ' || qt_idade_final_w;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
