SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_garu_dependencia_seg ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p
C - Beneficiário do contrato
P - Beneficiário da proposta
S - Beneficiário da simuação

*/
ds_retorno_w		varchar(10);
nr_seq_titular_w	bigint;
nr_seq_titular_prop_w	bigint;
nr_seq_grau_parent_w	bigint;
ie_tipo_parentesco_w	varchar(2);
ie_tipo_benef_w		varchar(1);
IE_DEPENDENTE_A100_w	varchar(10);


BEGIN

if (ie_opcao_p = 'C') then
	begin
	select	nr_seq_titular,
		coalesce(ie_dependente_a100,'N')
	into STRICT	nr_seq_titular_w,
		ie_dependente_a100_w
	from	pls_segurado
	where	nr_sequencia	= nr_sequencia_p;


	exception
	when others then
		nr_seq_titular_w:= null;
		ie_dependente_a100_w:= 'N';
	end;
	if (coalesce(nr_seq_titular_w::text, '') = '') and (ie_dependente_a100_w = 'N')then
		ds_retorno_w	:= 'T';
	else
		select	nr_seq_parentesco,
			ie_tipo_parentesco
		into STRICT	nr_seq_grau_parent_w,
			ie_tipo_parentesco_w
		from	pls_segurado
		where	nr_sequencia	= nr_sequencia_p;

		if (coalesce(ie_tipo_parentesco_w::text, '') = '') then
			select	max(ie_tipo_parentesco)
			into STRICT	ie_tipo_parentesco_w
			from	grau_parentesco
			where	nr_sequencia	= nr_seq_grau_parent_w;
		end if;

		if (ie_tipo_parentesco_w = '1') then
			ds_retorno_w	:= 'DL';
		elsif (ie_tipo_parentesco_w = '2') then
			ds_retorno_w	:= 'DA';
		end if;
	end if;
elsif (ie_opcao_p = 'P') then
	select	max(nr_seq_titular)
	into STRICT	nr_seq_titular_prop_w
	from	pls_proposta_beneficiario
	where	nr_sequencia	= nr_sequencia_p;

	select	max(nr_seq_titular_contrato)
	into STRICT	nr_seq_titular_w
	from	pls_proposta_beneficiario
	where	nr_sequencia	= nr_sequencia_p;

	if (coalesce(nr_seq_titular_w::text, '') = '') and (coalesce(nr_seq_titular_prop_w::text, '') = '') then
		ds_retorno_w	:= 'T';
	else
		select	nr_seq_parentesco,
			ie_tipo_parentesco
		into STRICT	nr_seq_grau_parent_w,
			ie_tipo_parentesco_w
		from	pls_proposta_beneficiario
		where	nr_sequencia	= nr_sequencia_p;

		if (coalesce(ie_tipo_parentesco_w::text, '') = '') then
			select	max(ie_tipo_parentesco)
			into STRICT	ie_tipo_parentesco_w
			from	grau_parentesco
			where	nr_sequencia	= nr_seq_grau_parent_w;
		end if;

		if (ie_tipo_parentesco_w = '1') then
			ds_retorno_w	:= 'DL';
		elsif (ie_tipo_parentesco_w = '2') then
			ds_retorno_w	:= 'DA';
		end if;
	end if;
elsif (ie_opcao_p = 'S') then
	select	ie_tipo_benef
	into STRICT	ie_tipo_benef_w
	from	pls_simulpreco_individual
	where	nr_sequencia	= nr_sequencia_p;

	if (ie_tipo_benef_w = 'T') then
		ds_retorno_w	:= 'T';
	else
		select	nr_seq_parentesco,
			ie_tipo_parentesco
		into STRICT	nr_seq_grau_parent_w,
			ie_tipo_parentesco_w
		from	pls_simulpreco_individual
		where	nr_sequencia	= nr_sequencia_p;

		if (coalesce(ie_tipo_parentesco_w::text, '') = '') then
			select	max(ie_tipo_parentesco)
			into STRICT	ie_tipo_parentesco_w
			from	grau_parentesco
			where	nr_sequencia	= nr_seq_grau_parent_w;
		end if;

		if (ie_tipo_parentesco_w = '1') then
			ds_retorno_w	:= 'DL';
		elsif (ie_tipo_parentesco_w = '2') then
			ds_retorno_w	:= 'DA';
		end if;
	end if;
end if;

return	coalesce(ds_retorno_w,'');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_garu_dependencia_seg ( nr_sequencia_p bigint, ie_opcao_p text) FROM PUBLIC;

