CREATE OR REPLACE FUNCTION pls_obter_glosas_tipo_val (nr_seq_tipo_validacao_p pls_oc_cta_tipo_validacao.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retorna os códigos das glosas tratadas pelo tipo de validação
(Para utilização por enquanto no localizador de validações)
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_glosas_w	varchar(4000)	:= null;

c_glosas CURSOR(nr_seq_tipo_validacao_pc	pls_oc_cta_tipo_validacao.nr_sequencia%type) FOR
	SELECT	a.cd_motivo_tiss
	from	pls_oc_cta_tipo_val_glosa a
	where	a.nr_seq_tipo_validacao = nr_seq_tipo_validacao_pc
	order by
		a.cd_motivo_tiss;


BEGIN

for	r_c_glosas_w in c_glosas(nr_seq_tipo_validacao_p) loop
	if (coalesce(ds_glosas_w::text, '') = '') then
		ds_glosas_w	:= r_c_glosas_w.cd_motivo_tiss;
	else
		ds_glosas_w	:= ds_glosas_w || ', ' || r_c_glosas_w.cd_motivo_tiss;
	end if;
end loop;

return	ds_glosas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
