CREATE OR REPLACE FUNCTION pls_obter_glosa_item_contest ( nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, ie_opcao_p text, nr_seq_fatura_proc_mat_p bigint default null) RETURNS bigint AS $body$
DECLARE

					
/* 
ie_opcao_p
'QT'	 - Qt. glosa original
'VL'	 - Vl. glosa original
*/
vl_retorno_w		double precision := 0;
vl_faturado_w		double precision := 0;
nr_seq_conta_pos_w	bigint;
				
C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_conta_pos_estabelecido a
	where	a.nr_seq_conta_proc = nr_seq_conta_proc_p
	and	((a.ie_situacao	= 'A') or (coalesce(a.ie_situacao::text, '') = ''));
	
C02 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_conta_pos_estabelecido a
	where	a.nr_seq_conta_mat = nr_seq_conta_mat_p
	and	((a.ie_situacao	= 'A') or (coalesce(a.ie_situacao::text, '') = ''));
				

BEGIN
if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
	if (ie_opcao_p = 'QT') then
		select (coalesce(qt_procedimento_imp,0) - coalesce(qt_procedimento,0))
		into STRICT	vl_retorno_w
		from	pls_conta_proc
		where	nr_sequencia	= nr_seq_conta_proc_p;
		
		if (vl_retorno_w = 0) then
			select	coalesce(max(qt_original),0)
			into STRICT	vl_retorno_w
			from	pls_conta_pos_estabelecido
			where	nr_seq_conta_proc = nr_seq_conta_proc_p
			and	((ie_situacao	= 'A') or (coalesce(ie_situacao::text, '') = ''));
			
			if (coalesce(vl_retorno_w,0) = 0) then
				vl_retorno_w := 1;
			end if;
		end if;
		
	elsif (ie_opcao_p = 'VL') then
		select	coalesce(vl_glosa,0)
		into STRICT	vl_retorno_w
		from	pls_conta_proc
		where	nr_sequencia	= nr_seq_conta_proc_p;
		
		if (vl_retorno_w = 0) then
			if (coalesce(nr_seq_fatura_proc_mat_p::text, '') = '') then
				open C01;
				loop
				fetch C01 into	
					nr_seq_conta_pos_w;
				EXIT WHEN NOT FOUND; /* apply on C01 */
					begin
					select	coalesce(max(vl_faturado),0) + coalesce(max(vl_faturado_ndc),0)
					into STRICT	vl_faturado_w
					from	pls_fatura_proc
					where	coalesce(nr_seq_fat_proc_cancel::text, '') = ''
					and	nr_seq_conta_pos_estab	= nr_seq_conta_pos_w;
					
					vl_retorno_w := vl_retorno_w + vl_faturado_w;
					end;
				end loop;
				close C01;
			else
				select	coalesce(a.vl_faturado,0) + coalesce(a.vl_faturado_ndc,0) +
					coalesce((	select sum(p.vl_faturado) + sum(p.vl_faturado_ndc)
						from	pls_fatura_proc p 
						where 	p.nr_seq_conta_pos_estab = a.nr_seq_conta_pos_estab
						and	p.nr_seq_fatura_conta = a.nr_seq_fatura_conta
						and	p.ie_tipo_cobranca in ('3','4')),0)
				into STRICT	vl_retorno_w
				from	pls_fatura_proc	a
				where	a.nr_sequencia	= nr_seq_fatura_proc_mat_p;
			end if;
		end if;
	end if;
elsif (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then
	if (ie_opcao_p = 'QT') then
		select (qt_material_imp - qt_material)
		into STRICT	vl_retorno_w
		from	pls_conta_mat
		where	nr_sequencia	= nr_seq_conta_mat_p;
		
		if (vl_retorno_w = 0) then
			select	coalesce(max(qt_original),0)
			into STRICT	vl_retorno_w
			from	pls_conta_pos_estabelecido
			where	nr_seq_conta_mat = nr_seq_conta_mat_p
			and	((ie_situacao	= 'A') or (coalesce(ie_situacao::text, '') = ''));
			
			if (coalesce(vl_retorno_w,0) = 0) then
				vl_retorno_w := 1;
			end if;
		end if;
		
	elsif (ie_opcao_p = 'VL') then
		select	coalesce(vl_glosa,0)
		into STRICT	vl_retorno_w
		from	pls_conta_mat
		where	nr_sequencia	= nr_seq_conta_mat_p;
		
		if (vl_retorno_w = 0) then
			open C02;
			loop
			fetch C02 into	
				nr_seq_conta_pos_w;
			EXIT WHEN NOT FOUND; /* apply on C02 */
				begin
				select	coalesce(max(vl_faturado),0) + coalesce(max(vl_faturado_ndc),0)
				into STRICT	vl_faturado_w
				from	pls_fatura_mat
				where	coalesce(nr_seq_fat_mat_cancel::text, '') = ''
				and	nr_seq_conta_pos_estab	= nr_seq_conta_pos_w;
				
				vl_retorno_w := vl_retorno_w + vl_faturado_w;
				end;
			end loop;
			close C02;
		end if;
	end if;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
