CREATE OR REPLACE FUNCTION pls_obter_glosa_vinc_ocor_imp ( nr_seq_ocorrencia_p pls_ocorrencia.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
Opções:

C - Código da glosa
D - Descrição da glosa
*/
nr_seq_motivo_glosa_w	tiss_motivo_glosa.nr_sequencia%type;
ds_retorno_w		varchar(255);


BEGIN
ds_retorno_w	:= null;
if (nr_seq_ocorrencia_p IS NOT NULL AND nr_seq_ocorrencia_p::text <> '') then

	select	max(nr_seq_motivo_glosa)
	into STRICT	nr_seq_motivo_glosa_w
	from	pls_ocorrencia
	where	nr_sequencia	= nr_seq_ocorrencia_p;

	if (nr_seq_motivo_glosa_w IS NOT NULL AND nr_seq_motivo_glosa_w::text <> '') then

		if (ie_opcao_p	= 'C') then
			select	max(cd_motivo_tiss)
			into STRICT	ds_retorno_w
			from	tiss_motivo_glosa
			where	nr_sequencia	= nr_seq_motivo_glosa_w;
		elsif (ie_opcao_p = 'D') then
			select	max(ds_motivo_tiss)
			into STRICT	ds_retorno_w
			from	tiss_motivo_glosa
			where	nr_sequencia	= nr_seq_motivo_glosa_w;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
