CREATE OR REPLACE FUNCTION pls_obter_gp_tipo_desp_analise ( nr_seq_grupo_p bigint, nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, nr_seq_proc_partic_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1)	:= null;
ie_tipo_despesa_w	varchar(1);
nr_seq_regra_w		bigint;
qt_oco_despesa_w	bigint;
ie_tipo_despesa_aux_w	varchar(10);
ie_tipo_w		varchar(10);

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		CASE WHEN ie_tipo_w='P' THEN a.ie_tipo_despesa_proc  ELSE a.ie_tipo_despesa_mat END
	from	pls_oc_grupo_tipo_desp	a
	where	a.nr_seq_ocorrencia_grupo	= nr_seq_grupo_p
	and ((a.ie_tipo_despesa_proc IS NOT NULL AND a.ie_tipo_despesa_proc::text <> '') or ie_tipo_w = 'M')
	and ((a.ie_tipo_despesa_mat IS NOT NULL AND a.ie_tipo_despesa_mat::text <> '') or ie_tipo_w = 'P')
	order by
		coalesce(a.ie_tipo_despesa_mat,' '),
		coalesce(a.ie_tipo_despesa_proc,' ');


BEGIN

select	count(1)
into STRICT	qt_oco_despesa_w
from	pls_oc_grupo_tipo_desp
where	nr_seq_ocorrencia_grupo = nr_seq_grupo_p;

if (qt_oco_despesa_w = 0) then
	ie_retorno_w := 'S';
	goto final;
end if;

if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
	select	max(ie_tipo_despesa)
	into STRICT	ie_tipo_despesa_aux_w
	from	pls_conta_proc
	where	nr_sequencia	= nr_seq_conta_proc_p;

	ie_tipo_w	:= 'P';
end if;

if (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then
	select	max(ie_tipo_despesa)
	into STRICT	ie_tipo_despesa_aux_w
	from	pls_conta_mat
	where	nr_sequencia	= nr_seq_conta_mat_p;

	ie_tipo_w	:= 'M';
end if;

if (nr_seq_proc_partic_p IS NOT NULL AND nr_seq_proc_partic_p::text <> '') then
	select	max(b.ie_tipo_despesa)
	into STRICT	ie_tipo_despesa_aux_w
	from	pls_conta_proc		b,
		pls_proc_participante	a
	where	a.nr_seq_conta_proc	= b.nr_sequencia
	and	a.nr_sequencia		= nr_seq_proc_partic_p;

	ie_tipo_w	:= 'P';
end if;

ie_retorno_w	:= 'N';
open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	ie_tipo_despesa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_tipo_despesa_aux_w = ie_tipo_despesa_w) then
		ie_retorno_w	:= 'S';
		goto final;
	end if;
	end;
end loop;
close C01;

<<final>>
ie_retorno_w	:= coalesce(ie_retorno_w,'S');

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
